package webDriverPackage;

public class WhileloopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// The goal is to print number from 0 to 19
		
		// These are while loops, test will run numbers less than 20, x=x+1 will add 1 every run
		int x=0;
		
		while (x<20){
			
			System.out.println(x);	
			x=x+1;
		}
		
		

	}

}
